public void shutdownReconfigurationTask() {
  Thread tempThread;
  synchronized (reconfigLock) {
    shouldRun = false;
    if (reconfigThread == null) {
      return;
    }
    tempThread = reconfigThread;
    reconfigThread = null;
  }

  try {
    tempThread.join();
  } catch (InterruptedException e) {
  }
}
