public boolean isOnSameNodeGroup(Node node1, Node node2) {
  if (node1 == null || node2 == null) {
    return false;
  }
  netlock.readLock().lock();
  try {
    return isSameParents(node1, node2);
  } finally {
    netlock.readLock().unlock();
  }
}
