    localhost = InetAddress.getLocalHost().getCanonicalHostName();
  } catch (UnknownHostException e) {
    LOG.warn("Unable to determine local hostname -falling back to '{}'",
        LOCALHOST, e);
    localhost = LOCALHOST;
  }
  return localhost;
}

