public static String normalizeIP2HostName(String ipPort) {
  if (null == ipPort || !ipPortPattern.matcher(ipPort).matches()) {
    return ipPort;
  }

  InetSocketAddress address = createSocketAddr(ipPort);
  return getHostPortString(address);
}
