public static int getFreeSocketPort() {
  int port = 0;
  try {
    ServerSocket s = new ServerSocket(0);
    port = s.getLocalPort();
    s.close();
    return port;
  } catch (IOException e) {
    // Could not get a free port. Return default port 0.
  }
  return port;
}
