public synchronized List<String> resolve(List<String> names) {
  if (!initialized) {
    initialized = true;
    load();
  }

  List<String> results = new ArrayList<String>(names.size());
  for (String name : names) {
    String result = map.get(name);
    if (result != null) {
      results.add(result);
    } else {
      results.add(NetworkTopology.DEFAULT_RACK);
    }
  }
  return results;
}
