public void reloadCachedMappings() {
  Map<String, String> newMap = load();
  if (newMap == null) {
    LOG.error("Failed to reload the topology table.  The cached " +
        "mappings will not be cleared.");
  } else {
    synchronized(this) {
      map = newMap;
    }
  }
}
