private void sendLogLevelRequest()
    throws HadoopIllegalArgumentException, Exception {
  switch (operation) {
  case GETLEVEL:
    doGetLevel();
    break;
  case SETLEVEL:
    doSetLevel();
    break;
  default:
    throw new HadoopIllegalArgumentException(
      "Expect either -getlevel or -setlevel");
  }
}
