public static void joinUninterruptibly(Thread toJoin) {
  boolean interrupted = false;
  try {
    while (true) {
      try {
        toJoin.join();
        return;
      } catch (InterruptedException e) {
        interrupted = true;
        LOG.warn("interrupted while sleeping", e);
      }
    }
  } finally {
    if (interrupted) {
      Thread.currentThread().interrupt();
    }
  }
}
