public byte[] digest() {
  if (curPositionInStripe > 0) {
    digestOut.write(CrcUtil.intToBytes(curCompositeCrc), 0, CRC_SIZE_BYTES);
    curCompositeCrc = 0;
    curPositionInStripe = 0;
  }
  byte[] digestValue = digestOut.toByteArray();
  digestOut.reset();
  return digestValue;
}
