static String bashQuote(String arg) {
  StringBuilder buffer = new StringBuilder(arg.length() + 2);
  buffer.append('\'');
  buffer.append(arg.replace("'", "'\\''"));
  buffer.append('\'');
  return buffer.toString();
}
