public void run() {
  Process p = shell.getProcess();
  try {
    p.exitValue();
  } catch (Exception e) {
    //Process has not terminated.
    //So check if it has completed 
    //if not just destroy it.
    if (p != null && !shell.completed.get()) {
      shell.setTimedOut();
      p.destroy();
    }
  }
}

