public void run() {
  try {
    String line = errReader.readLine();
    while((line != null) && !isInterrupted()) {
      errMsg.append(line);
      errMsg.append(System.getProperty("line.separator"));
      line = errReader.readLine();
    }
  } catch(IOException ioe) {
    LOG.warn("Error reading the error stream", ioe);
  }
}
