private void renameSelf(String newSuffix) throws IOException {
  File src = file;
  File dst = new File(src.getParent(), src.getName() + newSuffix);
  boolean success = src.renameTo(dst);
  if (!success) {
    throw new IOException(
      "Couldn't rename log " + src + " to " + dst);
  }
  file = dst;
}
