public boolean isIn(String ipAddress) {
  if (ipAddress == null) {
    throw new IllegalArgumentException("ipAddress is null");
  }

  if (LOCALHOST_IP.equals(ipAddress)) {
    return true;
  }

  for (IPList networkList:networkLists) {
    if (networkList.isIn(ipAddress)) {
      return true;
    }
  }
  return false;
}
