{
  Preconditions.checkState(state.compareAndSet(State.LATENT, State.STARTED), "Cannot be started more than once");

  task = executor.scheduleWithFixedDelay
      (
          new Runnable()
          {
            @Override
            public void run()
            {
              doWork();
            }
          },
          reapingThresholdMs,
          reapingThresholdMs,
          TimeUnit.MILLISECONDS
      );

  reaper.start();
}
