public boolean create(final String path, List<ACL> zkAcl) throws Exception {
  boolean created = false;
  if (!exists(path)) {
    curator.create()
        .withMode(CreateMode.PERSISTENT)
        .withACL(zkAcl)
        .forPath(path, null);
    created = true;
  }
  return created;
}
