private void callAsyncGet(long timeout, TimeUnit unit) {
  if (!isCancelled() && called.compareAndSet(false, true)) {
    try {
      set(asyncGet.get(timeout, unit));
    } catch (TimeoutException te) {
      LOG.trace("TRACE", te);
      called.compareAndSet(true, false);
    } catch (Throwable e) {
      LOG.trace("TRACE", e);
      setException(e);
    }
  }
}
