public static SysInfo newInstance() {
  if (Shell.LINUX) {
    return new SysInfoLinux();
  }
  if (Shell.WINDOWS) {
    return new SysInfoWindows();
  }
  throw new UnsupportedOperationException("Could not determine OS");
}
