public void put(K k, V v) {
  Preconditions.checkNotNull(k);
  if (buffer == null) {
    realloc(DEFAULT_MAX_CAPACITY);
  } else if (numInserted + 1 > capacity) {
    realloc(capacity * 2);
  }
  putInternal(k, v);
}
