private int getElementIndex(K k) {
  if (buffer == null) {
    return -1;
  }
  final int numEntries = buffer.length / 2;
  int hash = System.identityHashCode(k);
  int index = hash % numEntries;
  int firstIndex = index;
  do {
    if (buffer[2 * index] == k) {
      return index;
    }
    index = (index + 1) % numEntries;
  } while (index != firstIndex);
  return -1;
}
