public static boolean popOption(String name, List<String> args) {
  for (Iterator<String> iter = args.iterator(); iter.hasNext(); ) {
    String cur = iter.next();
    if (cur.equals("--")) {
      // stop parsing arguments when you see --
      break;
    } else if (cur.equals(name)) {
      iter.remove();
      return true;
    }
  }
  return false;
}
