public static String popFirstNonOption(List<String> args) {
  for (Iterator<String> iter = args.iterator(); iter.hasNext(); ) {
    String cur = iter.next();
    if (cur.equals("--")) {
      if (!iter.hasNext()) {
        return null;
      }
      cur = iter.next();
      iter.remove();
      return cur;
    } else if (!cur.startsWith("-")) {
      iter.remove();
      return cur;
    }
  }
  return null;
}
