    int curBits = status.get();
    if ((curBits & STATUS_CLOSED_MASK) != 0) {
      throw new ClosedChannelException();
    }
    if (status.compareAndSet(curBits, curBits | STATUS_CLOSED_MASK)) {
      return curBits & (~STATUS_CLOSED_MASK);
    }
  }
}

