static long getShutdownTimeout(Configuration conf) {
  long duration = conf.getTimeDuration(
      SERVICE_SHUTDOWN_TIMEOUT,
      SERVICE_SHUTDOWN_TIMEOUT_DEFAULT,
      TIME_UNIT_DEFAULT);
  if (duration < TIMEOUT_MINIMUM) {
    duration = TIMEOUT_MINIMUM;
  }
  return duration;
}
