void stopReencryptThread() {
  if (getProvider() == null || reencryptionHandler == null) {
    return;
  }
  dir.writeLock();
  try {
    reencryptionHandler.stopThreads();
  } finally {
    dir.writeUnlock();
  }
  if (reencryptHandlerExecutor != null) {
    reencryptHandlerExecutor.shutdownNow();
    reencryptHandlerExecutor = null;
  }
}
