    addChunk(initialChunkCapacity);
  } else if (lastChunk.size() >= lastChunkCapacity) {
    int newCapacity = lastChunkCapacity + (lastChunkCapacity >> 1);
    addChunk(Math.min(newCapacity, maxChunkSize));
  }
  size++;
  return lastChunk.add(e);
}

