protected void serviceStop() throws Exception {
  shouldRun = false;
  if (monitorThread != null) {
    monitorThread.interrupt();
    try {
      monitorThread.join();
    } catch (InterruptedException e) {
      Thread.currentThread().interrupt();
    }
  }
  super.serviceStop();
}
