public static List<Field> getDeclaredFieldsIncludingInherited(Class<?> clazz) {
  List<Field> fields = new ArrayList<Field>();
  while (clazz != null) {
    for (Field field : clazz.getDeclaredFields()) {
      fields.add(field);
    }
    clazz = clazz.getSuperclass();
  }

  return fields;
}
