public static List<Method> getDeclaredMethodsIncludingInherited(Class<?> clazz) {
  List<Method> methods = new ArrayList<Method>();
  while (clazz != null) {
    for (Method method : clazz.getDeclaredMethods()) {
      methods.add(method);
    }
    clazz = clazz.getSuperclass();
  }

  return methods;
}
