private synchronized void stopSinks() {
  for (Entry<String, MetricsSinkAdapter> entry : sinks.entrySet()) {
    MetricsSinkAdapter sa = entry.getValue();
    LOG.debug("Stopping metrics sink "+ entry.getKey() +
        ": class=" + sa.sink().getClass());
    sa.stop();
  }
  sinks.clear();
}

