    try { stop(); }
    catch (Exception e) {
      LOG.warn("Error stopping the metrics system", e);
    }
  }
  allSources.clear();
  allSinks.clear();
  callbacks.clear();
  if (mbeanName != null) {
    MBeans.unregister(mbeanName);
    mbeanName = null;
  }
  LOG.info(prefix +" metrics system shutdown complete.");
  return true;
}
