private Configuration loadConf() {
  Configuration conf;

  if (suppliedConf != null) {
    conf = suppliedConf;
  } else {
    // The config we're handed in init() isn't the one we want here, so we
    // create a new one to pick up the full settings.
    conf = new Configuration();
  }

  return conf;
}
