private boolean checkAppend(FileSystem fs) {
  boolean canAppend = true;

  try {
    fs.append(basePath);
  } catch (IOException ex) {
    if (ex.getMessage().equals("Not supported")) {
      canAppend = false;
    }
  }

  return canAppend;
}
