public void flush() {
  // currentOutStream is null if currentFSOutStream is null
  if (currentFSOutStream != null) {
    try {
      currentFSOutStream.hflush();
    } catch (IOException ex) {
      throwMetricsException("Unable to flush log file", ex);
    }
  }
}
