public void close() {
  synchronized (lock) {
    if (currentOutStream != null) {
      currentOutStream.close();

      try {
        checkForErrors("Unable to close log file");
      } finally {
        // Null out the streams just in case someone tries to reuse us.
        currentOutStream = null;
        currentFSOutStream = null;
      }
    }
  }
}

