private static String encodeFeInfo(FileEncryptionInfo feInfo) {
  Encoder encoder = Base64.getEncoder();
  String encodedValue = encoder
      .encodeToString(PBHelperClient.convert(feInfo).toByteArray());
  return encodedValue;
}
