synchronized void replaceScheduledTask(int windows, long interval,
                                       TimeUnit timeUnit) {
  numWindows = windows;
  scheduledTask.cancel(true);
  scheduledTask = SCHEDULER.scheduleAtFixedRate(new RatesRoller(this),
      interval, interval, timeUnit);
}
