protected boolean shutdown() {
  Service service;
  boolean result = false;
  synchronized (this) {
    service = serviceRef.get();
    serviceRef.clear();
  }
  if (service != null) {
    try {
      // Stop the  Service
      service.stop();
      result = true;
    } catch (Throwable t) {
      LOG.info("Error stopping {}", service.getName(), t);
    }
  }
  return result;
}
