static void allowSnapshot(FSDirectory fsd, SnapshotManager snapshotManager,
                          String path) throws IOException {
  fsd.writeLock();
  try {
    snapshotManager.setSnapshottable(path, true);
  } finally {
    fsd.writeUnlock();
  }
  fsd.getEditLog().logAllowSnapshot(path);
}
