static void disallowSnapshot(
    FSDirectory fsd, SnapshotManager snapshotManager,
    String path) throws IOException {
  fsd.writeLock();
  try {
    snapshotManager.resetSnapshottable(path);
  } finally {
    fsd.writeUnlock();
  }
  fsd.getEditLog().logDisallowSnapshot(path);
}
