    snapshotManager.renameSnapshot(path, snapshotOldName, snapshotNewName);
  } finally {
    fsd.writeUnlock();
  }
  fsd.getEditLog().logRenameSnapshot(path, snapshotOldName,
      snapshotNewName, logRetryCache);
}
