protected <T extends Feature> T getFeature(Class<? extends Feature> clazz) {
  Preconditions.checkArgument(clazz != null);
  for (Feature f : features) {
    if (clazz.isAssignableFrom(f.getClass())) {
      @SuppressWarnings("unchecked")
      T ret = (T) f;
      return ret;
    }
  }
  return null;
}
