private void addToCacheAndBlockMap(ArrayList<INode> inodeList) {
  try {
    cacheNameMapLock.lock();
    for (INode i : inodeList) {
      dir.cacheName(i);
    }
  } finally {
    cacheNameMapLock.unlock();
  }

  try {
    blockMapLock.lock();
    for (INode i : inodeList) {
      updateBlocksMap(i.asFile(), fsn.getBlockManager());
    }
  } finally {
    blockMapLock.unlock();
  }
}
