private void save(OutputStream out, INode n) throws IOException {
  if (n.isDirectory()) {
    save(out, n.asDirectory());
  } else if (n.isFile()) {
    save(out, n.asFile());
  } else if (n.isSymlink()) {
    save(out, n.asSymlink());
  }
}
