  if (val == HdfsConstants.QUOTA_RESET) {
    tsCounts = STORAGE_TYPE_RESET;
  } else if (val == 0) {
    tsCounts = STORAGE_TYPE_DEFAULT;
  } else {
    tsCounts = modify(tsCounts, ec -> ec.reset(val));
  }
  return this;
}

