public long skip(long amt) throws IOException {
  long extra = (curPos + amt) - limitPos;
  if (extra > 0) {
    throw new IOException("Tried to skip " + extra + " bytes past " +
        "the limit at offset " + limitPos);
  }
  long ret = super.skip(amt);
  curPos += ret;
  return ret;
}

