private synchronized INode[] getINodesWithLease() {
  int inodeCount = 0;
  INode[] inodes = new INode[leasesById.size()];
  for (long inodeId : leasesById.keySet()) {
    inodes[inodeCount] = fsnamesystem.getFSDirectory().getInode(inodeId);
    inodeCount++;
  }
  return inodes;
}
