private void cleanupAndRethrowIOException(IOException ioe)
    throws IOException {
  if (exceptionNeedsCleanup(ioe)) {
    final MultipleIOException.Builder b = new MultipleIOException.Builder();
    b.add(ioe);
    try {
      completeFile();
    } catch (IOException e) {
      b.add(e);
      throw b.build();
    }
  }
  throw ioe;
}
