      resultingStat = dir.getFileInfo(src, false);
    }
  } finally {
    writeUnlock();
  }
  getEditLog().logSync();
  if (auditLog.isInfoEnabled() && isExternalInvocation()) {
    logAuditEvent(UserGroupInformation.getCurrentUser(),
                  Server.getRemoteIp(),
                  "setPermission", src, null, resultingStat);
  }
